import React, { useEffect, useState } from "react";
import { Line } from "react-chartjs-2";
import { socket } from "../socket";
import { Chart, registerables } from "chart.js";
Chart.register(...registerables);

function AireDashboard() {
  const [data, setData] = useState([]);

  useEffect(() => {
    let interval;

    try {
      // =====================================
      // üîå MODO BACKEND REAL (Socket.IO)
      // =====================================
      socket.on("nuevoDatoAire", (dato) => {
        // Cuando el backend est√© activo, cada "dato" vendr√° en formato:
        // { time: "...", object: { temperature, humidity, co2, pressure } }
        setData((prev) => [...prev.slice(-19), dato]);
      });

      // =====================================
      // üß™ MODO SIMULACI√ìN (sin backend)
      // =====================================
      interval = setInterval(() => {
        const simulacion = {
          time: new Date().toISOString(),
          object: {
            temperature: (20 + Math.random() * 8).toFixed(2),
            humidity: (40 + Math.random() * 30).toFixed(2),
            co2: (400 + Math.random() * 500).toFixed(0),
            pressure: (1010 + Math.random() * 15).toFixed(2),
          },
        };
        setData((prev) => [...prev.slice(-19), simulacion]);
      }, 1000);

    } catch (err) {
      console.warn("‚ö†Ô∏è Sin conexi√≥n al backend, usando datos simulados...");
    }

    // Limpieza al desmontar componente
    return () => {
      socket.off("nuevoDatoAire");
      clearInterval(interval);
    };
  }, []);

  // --- Configuraci√≥n del gr√°fico ---
  const chartData = {
    labels: data.map((d) => new Date(d.time).toLocaleTimeString()),
    datasets: [
      { label: "Temperatura (¬∞C)", data: data.map((d) => d.object.temperature), borderColor: "tomato", fill: false },
      { label: "Humedad (%)", data: data.map((d) => d.object.humidity), borderColor: "skyblue", fill: false },
      { label: "CO‚ÇÇ (ppm)", data: data.map((d) => d.object.co2), borderColor: "limegreen", fill: false },
    ],
  };

  return (
    <div className="dashboard">
      <h2>üå´Ô∏è Sensor de Calidad del Aire</h2>
      <p style={{ color: "gray" }}>üîÅ Datos simulados (1 actualizaci√≥n por segundo)</p>
      <Line data={chartData} />
    </div>
  );
}

export default AireDashboard;
