import React, { useEffect, useState } from "react";
import { Line, Bar } from "react-chartjs-2";
import { socket } from "../socket";
import { Chart, registerables } from "chart.js";
Chart.register(...registerables);

function SonidoDashboard() {
  const [data, setData] = useState([]);

  useEffect(() => {
    let interval;
    try {
      // üîå Backend real (cuando est√© activo)
      socket.on("nuevoDatoSonido", (dato) => {
        // Los datos reales tendr√°n estructura:
        // { time, object: { laeq, laiMax, battery } }
        setData((prev) => [...prev.slice(-49), dato]);
      });

      // üß™ Simulaci√≥n temporal
      interval = setInterval(() => {
        const simulacion = {
          time: new Date().toISOString(),
          object: {
            laeq: (60 + Math.random() * 20).toFixed(2),
            laiMax: (75 + Math.random() * 20).toFixed(2),
            battery: (70 + Math.random() * 30).toFixed(2),
          },
        };
        setData((prev) => [...prev.slice(-49), simulacion]);
      }, 1000);
    } catch (err) {
      console.warn("‚ö†Ô∏è Sin conexi√≥n al backend, usando datos simulados...");
    }

    return () => {
      socket.off("nuevoDatoSonido");
      clearInterval(interval);
    };
  }, []);

  // --- Datos base ---
  const labels = data.map((d) => new Date(d.time).toLocaleTimeString());

  // --- 1Ô∏è‚É£ Evoluci√≥n de niveles de ruido ---
  const noiseTrend = {
    labels,
    datasets: [
      { label: "LAeq (dB)", data: data.map((d) => d.object.laeq), borderColor: "orange", fill: false },
      { label: "LAiMax (dB)", data: data.map((d) => d.object.laiMax), borderColor: "red", fill: false },
    ],
  };

  // --- 2Ô∏è‚É£ Promedio de ruido (barras) ---
  const avgNoise = {
    labels: ["Sensor 1", "Sensor 2", "Sensor 3", "Sensor 4"],
    datasets: [
      { label: "Promedio LAeq (dB)", data: [72, 70, 68, 75], backgroundColor: "#26c6da" },
    ],
  };

  // --- 3Ô∏è‚É£ Nivel de bater√≠a ---
  const batteryTrend = {
    labels,
    datasets: [
      { label: "Nivel de bater√≠a (%)", data: data.map((d) => d.object.battery), borderColor: "gray", fill: false },
    ],
  };

  // --- 4Ô∏è‚É£ Histograma de LAeq ---
  const laeqHist = {
    labels: Array.from({ length: 10 }, (_, i) => `Rango ${i + 1}`),
    datasets: [
      { label: "Distribuci√≥n LAeq (dB)", data: data.slice(0, 10).map((d) => d.object.laeq), backgroundColor: "#42a5f5" },
    ],
  };

  return (
    <div className="dashboard">
      <h2>üîä Sensor de Sonido</h2>
      <p style={{ color: "gray" }}>üîÅ Datos simulados (1 actualizaci√≥n por segundo)</p>

      <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: "25px", marginTop: "20px" }}>
        {/* 1Ô∏è‚É£ Niveles de ruido */}
        <div style={{ backgroundColor: "#1e1e1e", padding: 20, borderRadius: 10 }}>
          <Line data={noiseTrend} />
        </div>

        {/* 2Ô∏è‚É£ Promedio por sensor */}
        <div style={{ backgroundColor: "#1e1e1e", padding: 20, borderRadius: 10 }}>
          <Bar data={avgNoise} />
        </div>

        {/* 3Ô∏è‚É£ Bater√≠a */}
        <div style={{ backgroundColor: "#1e1e1e", padding: 20, borderRadius: 10 }}>
          <Line data={batteryTrend} />
        </div>

        {/* 4Ô∏è‚É£ Histograma de LAeq */}
        <div style={{ backgroundColor: "#1e1e1e", padding: 20, borderRadius: 10 }}>
          <Bar data={laeqHist} />
        </div>
      </div>
    </div>
  );
}

export default SonidoDashboard;
