import React, { useEffect, useState } from "react";
import { Line } from "react-chartjs-2";
import { socket } from "../socket";
import { Chart, registerables } from "chart.js";
Chart.register(...registerables);

function SonidoDashboard() {
  const [data, setData] = useState([]);

  useEffect(() => {
    let interval;

    try {
      // =====================================
      // üîå MODO BACKEND REAL (Socket.IO)
      // =====================================
      socket.on("nuevoDatoSonido", (dato) => {
        // Cuando el backend est√© activo, el objeto ser√° similar a:
        // { time, object: { laeq, laiMax, battery } }
        setData((prev) => [...prev.slice(-19), dato]);
      });

      // =====================================
      // üß™ MODO SIMULACI√ìN
      // =====================================
      interval = setInterval(() => {
        const simulacion = {
          time: new Date().toISOString(),
          object: {
            laeq: (60 + Math.random() * 20).toFixed(2),
            laiMax: (75 + Math.random() * 20).toFixed(2),
            battery: (70 + Math.random() * 30).toFixed(2),
          },
        };
        setData((prev) => [...prev.slice(-19), simulacion]);
      }, 1000);

    } catch (err) {
      console.warn("‚ö†Ô∏è Sin conexi√≥n al backend, usando datos simulados...");
    }

    return () => {
      socket.off("nuevoDatoSonido");
      clearInterval(interval);
    };
  }, []);

  const chartData = {
    labels: data.map((d) => new Date(d.time).toLocaleTimeString()),
    datasets: [
      { label: "LAeq (dB)", data: data.map((d) => d.object.laeq), borderColor: "orange", fill: false },
      { label: "LAiMax (dB)", data: data.map((d) => d.object.laiMax), borderColor: "red", fill: false },
      { label: "Bater√≠a (%)", data: data.map((d) => d.object.battery), borderColor: "gray", fill: false },
    ],
  };

  return (
    <div className="dashboard">
      <h2>üîä Sensor de Sonido</h2>
      <p style={{ color: "gray" }}>üîÅ Datos simulados (1 actualizaci√≥n por segundo)</p>
      <Line data={chartData} />
    </div>
  );
}

export default SonidoDashboard;
